$(document).ready(function () {
    $(".open-inf-right-menu").click(function () {
        $("html").css({"overflow":"hidden"});
        $("#inf-right-menu").show().children(".inf-right-menu-wrap").css({"right":"-300px"});
        $("#inf-right-menu").children(".inf-right-menu-wrap").animate({"right":"0px"},400);
    });
    $(".inf-right-menu-close, .inf-right-menu-overlay").click(function () {
        $("html").css({"overflow":""});
        $("#inf-right-menu").children(".inf-right-menu-wrap").animate({"right":"-300px"},400,function () {
            $("#inf-right-menu").hide();
        });
    });

});
