; /* Start:/bitrix/components/infostart/callback/templates/.default/script.js*/
$(document).ready(function () {
    $(".m-callback-close").click(function () {
        $(".m-callback").fadeOut();
        return false;
    });
    $(".m-callback-open").click(function () {
        if($(".m-callback:visible").length > 0){
            $(".m-callback").fadeOut();
        }else {
            $(".m-callback").fadeIn();
        }
        return false;
    });
    $("#m-callback").submit(function () {
        $("#m-callback input[name=NUMBER]").css('border-color','');
        if($("#m-callback input[name=NUMBER]").val().length < 10){
            $("#m-callback input[name=NUMBER]").css('border-color','red');
            return false;
        }
        $.get('/bitrix/ajax/callback.php?SESSID='+BX.bitrix_sessid()+'&ACTION=call&NUMBER='+$("#m-callback input[name=NUMBER]").val()+'&NAME='+$("#m-callback input[name=NAME]").val());

        $(this).next().show();
        $("#m-callback").remove();
        setTimeout(function () {
            $(".m-callback").fadeOut();
        },3000);
        return false;
    });


    $("#m-form-request-wrap").submit(function(){
        $("#m-form-request-wrap input, #m-form-request-wrap textarea").css('border-color','');
        formData = new FormData($("#m-form-request-wrap").get(0));
        $.ajax({
            url: '/ajax/send_landing_form.php?ACTION=SEND_FORM&RESPONS=feedback&SESSID='+BX.bitrix_sessid(),
            type: 'post',
            contentType: false,
            processData: false,
            data: formData,
            dataType: 'json',
            success: function(arResult){
                if(arResult.ERROR){
                    $("#m-form-request-wrap input[name="+arResult.FIELD+"], #m-form-request-wrap textarea[name="+arResult.FIELD+"]").css('border-color','#e4aeae');
                }else{
                    $("#m-form-request-wrap").next().show();
                    $("#m-form-request-wrap").remove();
                    setTimeout(function () {
                        $(".m-callback").fadeOut();
                    },3000);
                }
            }
        });
        return false;
    });
});
